let cart = [];

function generateProductCards() {
  const productContainer = document.getElementById("products");

  products.forEach((product) => {
    const productCard = document.createElement("div");
    productCard.classList.add("product");
    productCard.id = product.id;

    productCard.innerHTML = `          
    
    <h3>${product.name}</h3>
          <img
            src="${product.image}"
            alt="${product.name}"
          />
          <p>
            ${product.description}
          </p>
          <ul>
            <li>Price: $${product.price}</li>
            <li>Category:${product.category}</li>
            <li>Rating:${product.rating}</li>
          </ul>
          <div class="email-field">
            <label for="Email">${product.email}</label>
          </div>
          <button onclick="addToCart(${product.id})" id="${product.id}">Add to cart</button> `;
    productContainer.appendChild(productCard);
  });
}

generateProductCards(); // Generate products

const cartDiv = document.getElementById("cart");
const checkoutDiv = document.getElementById("checkout");

function openCart() {
  cartDiv.classList.add("open");
}
function closeCart() {
  cartDiv.classList.remove("open");
}

function renderCart() {
  const cartItems = document.getElementById("cart-items");
  const cartTotal = document.getElementById("cart-total");

  //clear cart
  cartItems.innerHTML = "";

  cart.forEach((product, index) => {
    const li = document.createElement("li");
    li.innerHTML = `
 ${product.name} - $${product.price} 
  <button onclick="removeFromCart(${index})">Remove</button>

`;
    cartItems.appendChild(li);
  });

  //Update Total
  const total = cart.reduce((sum, product) => sum + product.price, 0);
  totalRounded = total.toFixed(2);
  cartTotal.textContent = `Total: $${totalRounded}`;
}

function addToCart(productId) {
  const product = products.find((p) => p.id === productId);
  if (!product) return;

  cart.push(product);

  renderCart();
  openCart();
}

function removeFromCart(index) {
  cart.splice(index, 1);
  renderCart();
}

function buy() {
  console.log("buy");
  closeCart();
  openCheckout();
}

function openCheckout() {
  console.log("open checkout");
  checkoutDiv.classList.add("open");

  const checkoutItems = document.getElementById("checkout-items");
  const checkoutTotal = document.getElementById("checkout-total");

  //clear cart
  checkoutItems.innerHTML = "";

  cart.forEach((product) => {
    const li = document.createElement("li");
    li.innerHTML = ` ${product.name} - $${product.price} `;
    checkoutItems.appendChild(li);
  });

   //Update Total
  const total = cart.reduce((sum, product) => sum + product.price, 0);
  totalRounded = total.toFixed(2);
  checkoutTotal.textContent = `Total: $${totalRounded}`;
}
